import { createSlice } from '@reduxjs/toolkit';
import axios from 'axios';

export const dataSlice = createSlice({
    name: 'data',
    initialState: {
        fish: [],
        bugs: [],
        seaCreatures: []
    },
    reducers: {
        setFishData: (state) => {
            const fishURL = 'https://acnhapi.com/v1/fish';
            const fishReq = axios.get(fishURL);
            fishReq.then(res => {
                state.fish = res.data;
            });
        },
        getFishData: (state) => {
            return state.fish
        },
        setBugData: (state) => {
            const bugURL = 'https://acnhapi.com/v1/bugs';
            const bugReq = axios.get(bugURL);
            bugReq.then(res => {
                state.bugs = res.data;
            });
        },
        getBugData: (state) => {
            return state.bugs
        },
        setSeaCreaturesData: (state) => {
            const seaURL = 'https://acnhapi.com/v1/sea';
            const seaReq = axios.get(seaURL);
            seaReq.then(res => {
                state.seaCreatures = res.data;
            });
        },
        getSeaCreaturesData: (state) => {
            return state.seaCreatures
        },
    }
});

export const { 
    setFishData, getFishData, 
    setBugData, getBugData, 
    setSeaCreaturesData, getSeaCreaturesData } = dataSlice.actions;

export default dataSlice.reducer;
