import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { fetchFishData } from './app/fishData/dataSlice';
import { fetchBugData } from './app/bugData/dataSlice';
import { Header } from './components/Header/Header';
import { HomePage } from './HomePage';
import {
  BrowserRouter as Router,
  Route
} from "react-router-dom";
import './App.css';

function App() {
    const fish = useSelector((state) => state.fishData.fish);
    const fishStatus = useSelector(state => state.fishData.status);

    const bugs = useSelector((state) => state.bugData.bugs);
    const bugStatus = useSelector(state => state.bugData.status);

  //  const seaCreatures = useSelector((state) => state.data.seaCreatures);
    const dispatch = useDispatch();

    useEffect(() => {
      console.log(fishStatus);
      if (fishStatus == 'idle') {
        dispatch(fetchFishData());
      }

      console.log(bugStatus);
      if (bugStatus == 'idle') {
        dispatch(fetchBugData());
      }
    //  dispatch(setBugData());
    //  dispatch(setSeaCreaturesData());
      console.log('fish: ', fish);
      console.log('bugs: ', bugs);
    //  console.log('bugs: ', bugs);
     // console.log('sea creatures: ', seaCreatures);
    }, [fishStatus, bugStatus, dispatch])

  return (
    <Router>
    <div className="App">
    <Header />
        <Route exact path="/" component={HomePage} />
    </div>
    </Router>
  );
}

export default App;
